<template>
  <div class="input-phone">
    <div
      class="countries__dropdown"
      :class="{ 'open': open }"
    >
      <div
        class="countries__selection"
        tabindex="0"
        @keyup.enter="open = !open"
        @click="open = !open"
      >
        <div class="countries__flag" :class="selectedCountry.id"></div>
      </div>
      <ul class="countries__dropdown-list">
        <li
          :key="item.id"
          v-for="item in countriesList"
          class="countries__dropdown-item"
          :class="{ 'active': selectedCountry.id === item.id }"
          tabindex="0"
          @keyup.enter="switchMask(item)"
          @click="switchMask(item)"
        >
          <div
            class="countries__flag"
            :class="item.id"
          ></div>
          <strong>{{ item.name }}</strong>
          <span>{{ item.code }}</span>
        </li>
      </ul>
    </div>
    <div class="countries__code">
      {{ selectedCountry.code }}
    </div>
    <input
      :id="inputName"
      ref="phoneField"
      class="input__field mb-0"
      type="text"
      v-model="value"
      v-phone
      :placeholder="selectedCountry.mask"
      @input="inputValue"
    />
    <input class="field-hide" type="hidden" :name="inputName" :value="fullValue" :required="required" tabindex="-1"/>
  </div>
</template>

<script>
import { getField } from '@/assets/helpers/get_field';

export default {
  name: 'PhoneField',
  directives: {
    phone: {
      bind: (el, binding, vnode) => {
        el.oninput = function (e) {
          if (!e.isTrusted) {
            return;
          }
          this.value = vnode.context.parsePhoneValue(this.value);
          let event = document.createEvent('Event');
          event.initEvent('input', false, true);
          el.dispatchEvent(event);
        }
      }
    }
  },
  data() {
    return {
      value: null,
      open: false,
      countriesList: [
        {
          id: 'am',
          name: 'Armenia (Հայաստան)',
          code: '+374',
          mask: '___ ___ __ __'
        },
        {
          id: 'by',
          name: 'Belarus (Беларусь)',
          code: '+375',
          mask: '__ ___ __ __'
        },
        {
          id: 'kz',
          name: 'Kazakhstan (Казахстан)',
          code: '+77',
          mask: '__ ___ __ __'
        },
        {
          id: 'kg',
          name: 'Kyrgyzstan (Кыргызстан)',
          code: '+996',
          mask: '__ ___ __ __'
        },
        {
          id: 'ru',
          name: 'Russia (Россия)',
          code: '+7',
          mask: '___ ___ __ __'
        },
        {
          id: 'ua',
          name: 'Ukraine (Україна)',
          code: '+380',
          mask: '___ __ __ __'
        }
      ],
      selectedCountry: {
        id: 'ru',
        name: 'Russia (Россия)',
        code: '+7',
        mask: '___ ___ __ __'
      }
    }
  },
  props: {
    inputName: {
      type: String,
      required: true
    },
    inputLabel: {
      type: String
    },
    required: {
      type: Boolean
    },
    defaultPhone: {
      type: String
    }
  },
  computed: {
    preformatById() {
      const collection = {
        am: /(\d{0,3})(\d{0,3})(\d{0,2})(\d{0,2})/,
        by: /(\d{0,2})(\d{0,3})(\d{0,2})(\d{0,2})/,
        kg: /(\d{0,3})(\d{0,2})(\d{0,2})(\d{0,2})/,
        kz: /(\d{0,2})(\d{0,3})(\d{0,2})(\d{0,2})/,
        ru: /(\d{0,3})(\d{0,3})(\d{0,2})(\d{0,2})/,
        ua: /(\d{0,2})(\d{0,3})(\d{0,2})(\d{0,2})/
      };
      return getField(collection, this.selectedCountry.id);
    },
    fullValue() {
      return this.value ? `${this.selectedCountry.code.replace(/\D/g, '')}${this.value.replace(/\D/g, '')}` : null;
    }
  },
  mounted() {
    document.addEventListener('click', this.checkTargetClick);
    if (this.defaultPhone) {
      const country = this.countriesList.find(item => this.defaultPhone.indexOf(item.code) === 0);
      if (country) {
        this.selectedCountry = country;
        this.value = this.parsePhoneValue(this.defaultPhone.slice(country.code.length));
      }
    }
    this.updateMask('mounted');
  },
  methods: {
    inputValue() {
      this.$emit('phoneFieldInputted', this.fullValue);
    },
    checkTargetClick(event) {
      if (!event.target.classList.contains('countries__selection')) {
        this.open = false;
      }
    },
    parsePhoneValue(value) {
      let x = value.replaceAll(/\D/g, '').match(this.preformatById);
      return x !== null ? !x[2] ? x[1] : x[1] + ' ' + x[2] + (x[3] ? ' ' + x[3] + (x[4] ? ' ' + x[4] : '') : '') : null;
    },
    switchMask(item) {
      this.selectedCountry = item;
      if (this.value) {
        this.value = this.parsePhoneValue(this.value.replaceAll(/\D/g, ''));
      }
    },
    updateMask(type) {
      const valueLength = this.selectedCountry.code.replaceAll('+', '').length + this.selectedCountry.mask.replaceAll(/[^_]/g, '').length;
      this.$emit('updateMask', { maskLength: valueLength, maskType: type });
    }
  },
  watch: {
    value() {
      if (this.open) {
        this.open = false;
      }
    },
    selectedCountry() {
      this.inputValue();
      this.updateMask('change');
    }
  },
  beforeDestroy() {
    document.removeEventListener('click', this.checkTargetClick);
  }
}
</script>

